import axios from "axios";
import { defineStore } from "pinia";

export const UseAssetStore = defineStore("task", {
    state: () => ({
        assets: [],
    }),
    getters: {
        getAssets(state){
            return state.assets
        }
    },
    actions: {
        async fetchAssets(){
            try{
                const res = await axios.get("http://localhost:5000/assets")
                //console.log(res)
                this.assets = res.data
            }catch(error){
                console.log(error)
            }
        }
    }
})
